import React from 'react'
import {Link, useLocation} from "react-router-dom"
import { HomeIcon } from '@heroicons/react/solid';
function Header () {
    const [timeLeft, setTimeLeft] = useState(500); // Time left in seconds
    const [isExtended, setIsExtended] = useState(false);
    const timer = useRef(null);

    const resetTimer = () => {
      if (timer.current) {
        clearTimeout(timer.current);
      }

      timer.current = setTimeout(() => {
        window.location.href = 'https://www.google.com';
      }, timeLeft * 10000); // Convert to milliseconds
    };

    const extendTimeout = () => {
      setIsExtended(true);
      setTimeLeft(prevTimeLeft => prevTimeLeft * 10);
    };

    useEffect(() => {
      if (timeLeft > 0) {
        const id = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
        return () => clearTimeout(id);
      }
    }, [timeLeft]);

    useEffect(() => {
      document.addEventListener('click', resetTimer);
      resetTimer(); // Start the timer for the first time

      return () => {
        document.removeEventListener('click', resetTimer);
        if (timer.current) {
          clearTimeout(timer.current);
        }
      };
    }, []);


    const location = useLocation();

    // Don't render the header on the /excludePage route
    if (location.pathname === '/' || location.pathname === '/start') {
      return null;
    }
    return (
        <div className="flex w-full ml-4">
            <Link to={"/"}>
            <HomeIcon className="h-6 w-6 text-teal-400 bg-blue-100 rounded-xl" />
            {/* <button className=" flex flex-col items-center text-center font-serif shadow-md shadow-slate-400 p-2  border-2 border-gray-400  bg-teal-600 text-pink-50 justify-center rounded-2xl text-sm mt-2"> Home</button> */}
            </Link>
        </div>
    )
}
export default Header
